import React from 'react'
import {useState} from 'react'

export default function TextForm(props) {
    const countWords = (str)=>{
        let words;
        if (text===""){
            words= 0; 
        }else{
            words = str.trim().split(/\s+/).length;
        }
        return words
    }
    const handleOnChange = (event)=>{
        setText(event.target.value)
    }
    const handleUpClick = ()=>{
        let newtext = text.toUpperCase();
        setText(newtext)
        props.showAlert("Converted to Uppercase!","success")
    }
    const RemoveExtraSpaces = ()=>{
        let newtext = text.split(/[ ]+/);
        setText(newtext.join(" "))
        props.showAlert("Extra Space Removed!","success")
    }
    const handletextCopy = ()=>{
        var text = document.getElementById("myBox")
        text.select();
        navigator.clipboard.writeText(text.value);
        props.showAlert("Text Copied!","success")
    }
    const handleLoClick = ()=>{
        let newtext = text.toLowerCase();
        setText(newtext)
        props.showAlert("Converted to lowercase!","success")
    }
    const handleclearText = ()=>{
        let newtext = '';
        setText(newtext)
        props.showAlert("Text Cleared!","success")

    }
    const [text,setText] = useState("")
    return(
        <>
    <div className="container" style={{color:props.mode==='dark'?'white':'#042743'}}>
        <h1>{props.heading}</h1>
        <div className="mb-3">
            <textarea className="form-control" value={text} onChange={handleOnChange} style={{backgroundColor:props.mode==='dark'?'grey':'white',color:props.mode==='dark'?'white':'dark'}} id="myBox" rows="8"></textarea>
        </div>

        <button className="btn btn-primary mx-2" onClick={handleUpClick}>Convert to UpperCase</button>
        <button className="btn btn-primary mx-2" onClick={handleLoClick}>Convert to LowerCase</button>
        <button className="btn btn-primary mx-2" onClick={handleclearText}>Clear Text</button>
        <button className="btn btn-primary mx-2" onClick={handletextCopy}>Copy Text</button>
        <button className="btn btn-primary mx-2" onClick={RemoveExtraSpaces}>Remove Extra Spaces</button>
    </div>
    <div className="container my-3" style={{color:props.mode==='dark'?'white':'black'}}>
        <h2> Your Text Summary</h2>
        <p>{countWords(text)} words and {text.length} characters</p>
        <p>{0.008 * countWords(text)} Minutes Read</p>
        <h2>Preview</h2>
        <p>{text.length>0?text:"Enter Something to preview it here"}</p>
    </div>
    </>
        )
}
